import styled from '@emotion/styled';

export const TrasactionTable = styled.table`
  width: 100%;
  thead {
    background-color: #3f9aef;
    color: #fff;
    height: 50px;
  }

  tbody {
    tr {
      text-align: center;
      td {
        font-size: 19px;
        height: 50px;
        // background-color: white;
      }
    }
  }
`;
